const input=document.querySelector("input"),checkForImage=()=>new Promise((e,t)=>{0===input.files.length?(document.querySelector(".main-section").innerHTML='\n      <h3 class="vertical-center"> Please upload one image </h3> \n      <div class="button-container"> \n        <button onclick="location.reload()">Retry</button> \n      </div>',t("Please upload one image")):e()}),getImage=()=>new Promise((e,t)=>{const n=new FileReader;n.onload=t=>e(document.querySelector("img").setAttribute("src",t.target.result)),n.readAsDataURL(input.files[0])});export{checkForImage,getImage};