const getImage=()=>new Promise((e,n)=>{const t=document.querySelector("input");0===t.files.length?(document.querySelector(".main-section").innerHTML='\n      <h3 class="vertical-center"> Please upload one image </h3> \n      <div class="button-container"> \n        <button onclick="location.reload()">Retry</button> \n      </div>',n("Please upload an file in JPG or PNG format")):e(t.files[0])});export default getImage;